import Nav from "../../components/nav";
import styles from './main.module.scss';
import { useEffect, useState, MouseEvent } from "react";

const Main = () => {
    const [handler, switchHandler] = useState(true);
    const [ballShift, changeBallShift] = useState(0);
    const listener = (e:any) => {
        console.log('scroll')
        if(pageYOffset < 90) {
            changeBallShift(pageYOffset)
            switchHandler(false)
        }
    }
    if(handler && document.body.scrollTop < 90) {
        document.addEventListener('scroll', listener)
    } else {
        document.removeEventListener('scroll', listener)
    }
    useEffect(() => {
        return () => {
            !handler && document.body.scrollTop > 90 ? document.removeEventListener('scroll', listener) : null
        }
    })
    return (
        <>
            <header className={styles.header}>
                <Nav />
                <div className={styles.headerBg}>
                    <img src="static/images/female.svg" alt=""/>
                    <img src="static/images/ball.svg" alt="" style={{
                        right: `${150+ballShift}px`
                    }} />
                    <img src="static/images/male.svg" alt=""/>
                </div>
                <div className={styles.heading}>
                    <h3>Teaches FIFA 20</h3>
                    <h1>cyberclass: FIFA 20</h1>
                    <span className="topSeller">Top seller</span>
                    <img src="static/images/fifa20.svg" alt=""/>
                </div>
            </header>
            <p className={styles.p}></p>
        </>
    )
}

export default Main;
