import styles from './Slider.module.scss';
import { useRef, useState, useEffect } from 'react';

interface UIEvent {
    targetTouches: { pageX: number; pageY: number; }[];
    // etc...
}

const Slider = (props) => {
    const sliderRef = useRef(null);
    const slideRef = useRef(null);

    let slideWidth = null;
    let slidesCount = null;
    const [slideStyle,changeSlideStyle] = useState({});
    const [slideIndex, changeSlideIndex] = useState(0);
    const handleBack = () => {
        if(slideIndex !== 0) {
            changeSlideStyle({
                transform: 'translateX(' + -slideWidth * (slideIndex-1) + 'px)'
            })
            changeSlideIndex(slideIndex - 1)
        }
    }

    const handleNext = () => {
        if(slideIndex !== slidesCount - 1) {
            changeSlideStyle({
                transform: 'translateX(' + -slideWidth * (slideIndex + 1) + 'px)'
            })
            changeSlideIndex(slideIndex + 1)
        }
    }

    const handleTouch = (e:any) => {
        let touches = e.changedTouches;
        const touchDivision = touches[touches.length - 1].clientX - touches[0].clientX;
        if(touchDivision > slideWidth/2.5) {
            handleBack()
        } else if (touchDivision < slideWidth/2.5 && touchDivision < 0) {
            handleNext()
        }
        // if(slideIndex !== 0) {
        //     changeSlideStyle({
        //         transform: 'translateX(' + -slideWidth * (slideIndex-1) + 'px)'
        //     })
        //     changeSlideIndex(slideIndex - 1)
        // }
    }

    useEffect(() => {
        if(slideRef) {
            slideWidth = slideRef.current.getBoundingClientRect().width;
            slidesCount = Math.ceil(slideRef.current.scrollWidth / sliderRef.current.getBoundingClientRect().width);
        }
    })

    return(
        <div className={styles.slider} ref={sliderRef}>
            <div className={styles.slider__container} ref={slideRef} style={slideStyle} onTouchMove={handleTouch}>
                {/* <div className={styles.slider__innerContainer}> */}
                    { props.children }
                {/* </div> */}
            </div>
            <div className={styles.slider__btnWrap}>
                <button className={styles.slider__btn + ' ' + styles.slider__btnPrev} onClick={handleBack}>
                    <img src="/static/images/arrow-left.svg" alt=""/>
                </button>
                <button className={styles.slider__btn + ' ' + styles.slider__btnNext} onClick={handleNext}>
                    <img src="/static/images/arrow-left.svg" alt=""/>
                </button>
            </div>
        </div>
    );
}

export default Slider;
