import styles from './Slider.module.scss';
import { useRef, useState } from 'react';

const Slider = (props) => {
    const sliderRef = useRef(null);
    const slideRef = useRef(null);

    const slideWidth = slideRef.current.getBoundingClientRect().width;
    const slidesCount = Math.ceil(slideRef.current.getBoundingClientRect().width / sliderRef.current.getBoundingClientRect().width);
    const [slideIndex, changeSlideIndex] = useState(0);
    const handleBack = () => {
        if(slideIndex != 0) {
            changeSlideIndex(slideIndex - 1)
        }
    }

    const handleNext = () => {
        if(slideIndex != slidesCount - 1) {
            changeSlideIndex(slideIndex + 1)
        }
    }

    return(
        <div className={styles.slider} ref={sliderRef}>
            <div className={styles.slider__container} ref={slideRef} style={{
                left: slideWidth * slideIndex + 'px'
            }}>
                {/* <div className={styles.slider__innerContainer}> */}
                    { props.children }
                {/* </div> */}
            </div>
            <div className={styles.slider__btnWrap}>
                <button className={styles.slider__btn + ' ' + styles.slider__btnPrev} onClick={handleBack}>
                    <img src="/static/images/arrow-left.svg" alt=""/>
                </button>
                <button className={styles.slider__btn + ' ' + styles.slider__btnNext} onClick={handleNext}>
                    <img src="/static/images/arrow-left.svg" alt=""/>
                </button>
            </div>
        </div>
    );
}

export default Slider;
