import axios from 'axios';
import { environment } from './environment/environment';
export const AuthAPI = {
  auth(login, password) {
    return axios.post(`${environment.apiEndPoint}/Account/AuthUser`, {
      login: login,
      password: password
    });
  },

  register(phone, password, email, nickName) {
    return axios.post(`${environment.apiEndPoint}/Account/RegisterUser`, {
      phoneNumber: phone,
      password: password,
      email: email,
      nickName: nickName
    });
  },

  sendActivationCode(login) {
    return axios.get(`${environment.apiEndPoint}/Code/SendActivationCode`, {
      params: {
        login: login
      }
    });
  },

  checkCode(login, code) {
    return axios.get(`${environment.apiEndPoint}/Code/CheckCode`, {
      params: {
        code: code,
        login: login
      }
    });
  },

  subscribeToNews(email) {
    return axios.post(`${environment.apiEndPoint}/Sub/CreateSub`, {
      email: email
    });
  }

};
