import React, { useState, FormEvent } from 'react';
import styles from './Profile.module.scss';
import Link from 'next/link';

const Profile = () => {

    const [profile, setProfile] = useState(null);

    const onSelectFile = () => {

    }

    const editProfile = (e:FormEvent) => {
        e.preventDefault();
    }

    return (
        <div className={styles.profile}>
            <h1>Profile</h1>
            <form className={styles['profile-form']} onSubmit={editProfile}>
                <div className={styles['avatar-container']}>
                    <img src={ profile?.person?.photoUrl ? 'http://194.177.23.9:555/' + profile?.person?.photoUrl : '/static/images/avatar.svg'} className={styles.avatar} />
                    <button type="button" className={styles['change-photo-btn']}>
                        Change photo
                        <input type="file" accept="image/x-png,image/gif,image/jpeg" onChange={onSelectFile} disabled={profile?.photoUrl} />
                    </button>
                    <Link href="/lesson">
                        <button className={styles['courses-link'] + ' common-btn'}>Мои курсы</button>
                    </Link>
                </div>
                <fieldset>
                    <input type="text" className="common-input" placeholder="Your Nickname" />
                    <input type="text" className="common-input" placeholder="Your Email" />
                    <input type="text" className="common-input" placeholder="Facebook" />
                    <input type="text" className="common-input" placeholder="http://vk.bobik.com" />
                    <input type="text" className="common-input" placeholder="Instagram" />
                    <input type="text" className="common-input" placeholder="Twich" />
                    <button type="submit" className="purple-btn">Save</button>
                </fieldset>
            </form>
        </div>
    )
}

export default Profile;
