import styles from './Slider.module.scss';
import { useRef, useState } from 'react';

const Slider = (props) => {
    const slideRef = useRef(null);

    const [slidePos, changeSlidePos] = useState(0);
    const handleBack = () => {
        changeSlidePos(slideRef ? slideRef.current.getBoundingClientRect().width : 0)
    }

    return(
        <div className={styles.slider}>
            <div className={styles.slider__container} ref={slideRef}>
                <div className={styles.slider__innerContainer}>
                    { props.children }
                </div>
            </div>
            <div className={styles.slider__btnWrap}>
                <button className={styles.slider__btn + ' ' + styles.slider__btnPrev}>
                    <img src="/static/images/arrow-left.svg" alt=""/>
                </button>
                <button className={styles.slider__btn + ' ' + styles.slider__btnNext}>
                    <img src="/static/images/arrow-left.svg" alt=""/>
                </button>
            </div>
        </div>
    );
}

export default Slider;
