import React, { useState, FormEvent } from 'react';
import styles from './Profile.module.scss';
import Link from 'next/link';

const Profile = () => {

    const [profile, setProfile] = useState(null);

    const onSelectFile = () => {

    }

    const editProfile = (e:FormEvent) => {
        e.preventDefault();
    }

    return (
        <>
            <h1>Profile</h1>
            <form className={styles['profile-form']} onSubmit={editProfile}>
                <div className={styles['avatar-container']}>
                    <img src={'http://194.177.23.9:555/' + profile?.person?.photoUrl} alt=" " className={styles.avatar} />
                    <input type="file" accept="image/x-png,image/gif,image/jpeg" value="Change photo" onChange={onSelectFile} disabled={profile.photoUrl} />
                    <Link href="/lesson">
                        <button className="common-btn">Мои курсы</button>
                    </Link>
                </div>
                <fieldset>
                    <input type="text" className="common-input" placeholder="Your Nickname" />
                    <input type="text" className="common-input" placeholder="Your Email" />
                    <input type="text" className="common-input" />
                    <input type="text" className="common-input" />
                    <input type="text" className="common-input" />
                    <input type="text" className="common-input" />
                    <button className="purple-btn">Save</button>
                </fieldset>
            </form>
        </>
    )
}

export default Profile;
