import { ActionsConstants } from "../models/actions-constants.model";


export const LessonsReducer = (state:[]=[], action:any) => {
    switch (action.type) {
        case ActionsConstants.SET_LESSONS:
            return [...action.lessons]
    
        default:
            return state
    }
}
