import React, { useEffect, useState } from 'react';
import Link from 'next/link';
import styles from './Header.module.scss';

const Header = ({store}) => {

    const [user,setUser] = useState(null);
    const [menuVision, toggleMenuVision] = useState(false);

    const logout = () => {
        localStorage.clear();
        setUser(null)
    }

    useEffect(() => {
        user === null ? setUser(JSON.parse(localStorage.getItem('user'))) : null;
    })

    return (
        <header className={styles.header} id="header">
            <div className={styles.header__wrap}>

                <div className={styles.header__logo}>
                    <Link href="/main">
                        <a className={styles.header__logo_link}>
                            <img src="/static/images/navigation/logo.svg" alt="" />
                        </a>
                    </Link>
                </div>

                <nav className={styles.header__nav}>
                    <ul className={styles.header__nav_list}>
                        <li className={styles.header__nav_item}>
                            {  !user?.userID ? 
                                <Link href="/authorization" >
                                    <div className={styles.header__nav_link}>
                                        <img src="/static/images/navigation/login.svg" alt="" />
                                        <span>Log in</span>
                                    </div>
                                </Link> :
                                <p className={styles['user-name']} onClick={() => toggleMenuVision(!menuVision)}>
                                    <img src={ user?.avatar ? 'http://194.177.23.9:577/' + user?.avatar : '/static/images/avatar.svg'} alt=""/>
                                    {user?.person?.phoneNumber || user?.nickname}
                                    { menuVision ? <p className={styles.logout} onClick={logout}><span>Logout</span></p> : null }
                                </p>
                            }
                        </li>
                    </ul>
                </nav>
            </div>
        </header>
    )
}

export default Header;
